    echo "Iteration: $ITERATION/$MAX_ITERATIONS"
    echo ""
    
    # Check for trigger
    TRIGGER_EXISTS=$(gcloud builds triggers list --project=$PROJECT_ID --format="value(name)" 2>&1 | grep -i "develop\|deploy" || echo "")
    
    # Check latest build
    BUILD_DATA=$(gcloud builds list --project=$PROJECT_ID --limit=1 --format="value(id,status,buildTriggerId)" 2>&1)
    
    if [ -z "$BUILD_DATA" ]; then
        echo "‚è≥ No builds found. Checking for trigger..."
        if [ -z "$TRIGGER_EXISTS" ]; then
            echo "‚ùå No trigger found. Waiting for trigger to be created..."
        else
            echo "‚úÖ Trigger found: $TRIGGER_EXISTS"
            echo "üì§ Pushing commit to trigger build..."
            echo "# Build attempt $ITERATION - $(date)" >> .build-test
            git add .build-test && git commit -m "chore: Build attempt $ITERATION" && git push origin develop 2>&1 | tail -3
        fi
    else
        BUILD_ID=$(echo $BUILD_DATA | cut -d' ' -f1)
        STATUS=$(echo $BUILD_DATA | cut -d' ' -f2)
        TRIGGER_ID=$(echo $BUILD_DATA | cut -d' ' -f3)
        
        echo "üìä Latest Build: $BUILD_ID"
        echo "   Status: $STATUS"
        if [ -n "$TRIGGER_ID" ] && [ "$TRIGGER_ID" != "null" ] && [ "$TRIGGER_ID" != "" ]; then
            echo "   ‚úÖ Trigger-based build"
        else
            echo "   ‚ö†Ô∏è  Manual build (will be blocked)"
        fi
        
        if [ "$STATUS" = "SUCCESS" ]; then
            echo ""
            echo "üéâ SUCCESS! Build completed successfully!"
            echo "   Cloud Run deployment should be live"
            exit 0
        elif [ "$STATUS" = "FAILURE" ] || [ "$STATUS" = "TIMEOUT" ]; then
            echo ""
            echo "‚ùå Build failed. Checking error logs..."
            ERROR_LOG=$(gcloud builds log "$BUILD_ID" --project=$PROJECT_ID 2>&1 | grep -A 10 -E "ERROR|FAILED|error" | tail -20)
            echo "$ERROR_LOG"
            
            # Check if it's a safeguard block
            if echo "$ERROR_LOG" | grep -q "BUILD_TRIGGER_ID"; then
                echo ""
                echo "‚ö†Ô∏è  Build was blocked by safeguard (manual build)"
                if [ -z "$TRIGGER_EXISTS" ]; then
                    echo "üí° Trigger needs to be created via Cloud Console"
                fi
            else
                echo ""
                echo "üîß Build failed with errors. Pushing fix..."
                # Add timestamp to trigger new build
                echo "# Fix attempt $ITERATION - $(date)" >> .build-test
                git add .build-test && git commit -m "fix: Build fix attempt $ITERATION" && git push origin develop 2>&1 | tail -3
            fi
        elif [ "$STATUS" = "WORKING" ] || [ "$STATUS" = "QUEUED" ]; then
            echo "‚è≥ Build in progress..."
        fi
    fi
    
    echo ""
    echo "‚è≥ Waiting 30 seconds..."
    sleep 30
done

echo ""
echo "‚ö†Ô∏è  Max iterations reached. Stopping monitoring."
