        sleep 30
        continue
    fi
    
    STATUS=$(gcloud builds describe "$BUILD_ID" --project=$PROJECT_ID --format="value(status)" 2>&1)
    TRIGGER_ID=$(gcloud builds describe "$BUILD_ID" --project=$PROJECT_ID --format="value(buildTriggerId)" 2>&1)
    
    echo ""
    echo "📊 Build Status: $STATUS"
    echo "   Build ID: $BUILD_ID"
    if [ -n "$TRIGGER_ID" ] && [ "$TRIGGER_ID" != "null" ]; then
        echo "   ✅ Trigger-based build (ID: $TRIGGER_ID)"
    else
        echo "   ⚠️  Manual build (will be blocked by safeguard)"
    fi
    
    if [ "$STATUS" = "SUCCESS" ]; then
        echo ""
        echo "✅ BUILD SUCCESSFUL!"
        echo "   Deployment to Cloud Run should be complete"
        exit 0
    elif [ "$STATUS" = "FAILURE" ] || [ "$STATUS" = "TIMEOUT" ] || [ "$STATUS" = "CANCELLED" ]; then
        echo ""
        echo "❌ Build failed. Analyzing logs..."
        gcloud builds log "$BUILD_ID" --project=$PROJECT_ID 2>&1 | grep -A 30 -E "ERROR|FAILED|failed|error" | tail -50
        echo ""
        echo "🔧 Fixing issues and pushing update..."
        # Add fix logic here
        ATTEMPT=$((ATTEMPT + 1))
        if [ $ATTEMPT -ge $MAX_ATTEMPTS ]; then
            echo "❌ Max attempts reached. Stopping."
            exit 1
        fi
        sleep 10
    elif [ "$STATUS" = "WORKING" ] || [ "$STATUS" = "QUEUED" ]; then
        echo "⏳ Build in progress. Waiting..."
        sleep 30
    fi
    
    sleep 10
done
