echo ""
echo "üìã Checking trigger status..."
TRIGGER_EXISTS=$(gcloud builds triggers list --project=$PROJECT_ID --format="value(name)" 2>&1 | grep -i "$TRIGGER_NAME" || echo "")

if [ -n "$TRIGGER_EXISTS" ]; then
    echo "‚úÖ Trigger '$TRIGGER_NAME' exists"
    echo ""
    echo "üìä Trigger details:"
    gcloud builds triggers describe "$TRIGGER_NAME" --project=$PROJECT_ID --format="yaml" 2>&1 | head -20
    
    echo ""
    echo "üîç Checking recent builds..."
    LATEST_BUILD=$(gcloud builds list --project=$PROJECT_ID --limit=1 --format="value(id)" 2>&1)
    if [ -n "$LATEST_BUILD" ]; then
        BUILD_TRIGGER=$(gcloud builds describe "$LATEST_BUILD" --project=$PROJECT_ID --format="value(buildTriggerId)" 2>&1)
        if [ -n "$BUILD_TRIGGER" ] && [ "$BUILD_TRIGGER" != "null" ] && [ "$BUILD_TRIGGER" != "" ]; then
            echo "‚úÖ Latest build was triggered (BUILD_TRIGGER_ID: $BUILD_TRIGGER)"
        else
            echo "‚ö†Ô∏è  Latest build was NOT triggered (manual build or no trigger)"
        fi
    fi
else
    echo "‚ùå Trigger '$TRIGGER_NAME' NOT FOUND"
    echo ""
    echo "üí° Create trigger via Cloud Console:"
    echo "   https://console.cloud.google.com/cloud-build/triggers/add?project=$PROJECT_ID"
fi
